import React, { useState, useEffect, useCallback } from 'react';

const TextToSpeech = ({ text }) => {
  const [pitch, setPitch] = useState(1);
  const [rate, setRate] = useState(1);
  const [voices, setVoices] = useState([]);
  const [selectedVoiceURI, setSelectedVoiceURI] = useState('');
  const [isSpeaking, setIsSpeaking] = useState(false);
  const [persistedVoiceURI, setPersistedVoiceURI] = useState('');

  const handlePitchChange = (event) => {
    setPitch(event.target.value);
  };

  const handleRateChange = (event) => {
    setRate(event.target.value);
  };

  const handleVoiceChange = (event) => {
    const voiceURI = event.target.value;
    setSelectedVoiceURI(voiceURI);
    setPersistedVoiceURI(voiceURI);
  };

  const handleSpeak = () => {
    if (text && selectedVoiceURI) {
      if (isSpeaking) {
        speechSynthesis.cancel();
        setIsSpeaking(false);
      } else {
        speak();
      }
    } else {
      setSelectedVoiceURI('');
      setPersistedVoiceURI('');
    }
  };

  const handleSpeechEnd = useCallback(() => {
    setIsSpeaking(false);
  }, []);

  const speak = useCallback(() => {
    const utt = new SpeechSynthesisUtterance(text);
    utt.pitch = pitch;
    utterance.rate = rate;
    if (selectedVoiceURI) {
      const selectedVoice = voices.find((voice) => voice.voiceURI === selectedVoiceURI);
      if (selectedVoice) {
        utterance.voice = selectedVoice;
      }
    }
    utterance.addEventListener('end', handleSpeechEnd);
    setIsSpeaking(true);
    speechSynthesis.speak(utterance);
  }, [text, pitch, rate, selectedVoiceURI, voices, handleSpeechEnd]);

  useEffect(() => {
    const handleVoicesChanged = () => {
      const availableVoices = speechSynthesis.getVoices();
      setVoices(availableVoices);
    };

    speechSynthesis.addEventListener('voiceschanged', handleVoicesChanged);

    // getting the initial voices
    const availableVoices = speechSynthesis.getVoices();
    setVoices(availableVoices);

    if (persistedVoiceURI) {
      setSelectedVoiceURI(persistedVoiceURI);
    }

    return () => {
      speechSynthesis.removeEventListener('voiceschanged', handleVoicesChanged);
    };
  }, [persistedVoiceURI]);

  return (
    <div >
      <div>
        <label htmlFor="pitch">Pitch:</label>
        <input
          type="range"
          id="pitch"
          min="0.5"
          max="2"
          step="0.1"
          value={pitch}
          onChange={handlePitchChange}
        />
        <span>{pitch}</span>
      </div>
      <div>
        <label htmlFor="rate">Speed:</label>
        <input
          type="range"
          id="rate"
          min="0.5"
          max="2"
          step="0.1"
          value={rate}
          onChange={handleRateChange}
        />
        <span>{rate}</span>
      </div>
      <div>
        <label htmlFor="voice">Voice:</label>
        <select id="voice" value={selectedVoiceURI} onChange={handleVoiceChange} style={{ width: '80%' }}>
          <option value="">Seletct Voice</option>
          {voices.map((voice) => (
            <option key={voice.voiceURI} value={voice.voiceURI}>
              {`${voice.name} (${voice.lang})`}
            </option>
          ))}
        </select>
      </div>
      <button onClick={handleSpeak}>{isSpeaking ? 'Stop' : 'Speak'}</button>
    </div>
  );
};

export default TextToSpeech;
