const crypto = require('crypto');

const ENCRYPTION_ALGORITHM = 'aes-256-cbc';
const ENCRYPTION_KEY = 'your-secret-encryption-key';

const decrypt = (encryptedData) => {
  const [ivHex, encryptedDataHex] = encryptedData.split(':');
  const iv = Buffer.from(ivHex, 'hex');
  const encryptedDataBuffer = Buffer.from(encryptedDataHex, 'hex');
  const decipher = crypto.createDecipheriv(ENCRYPTION_ALGORITHM, Buffer.from(ENCRYPTION_KEY), iv);
  let decryptedData = decipher.update(encryptedDataBuffer);
  decryptedData = Buffer.concat([decryptedData, decipher.final()]);
  return decryptedData.toString();
};

module.exports = decrypt;
