import React, { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../pages/auth/Auth';
//import decrypt from '../crypto/Decryption';

const useIsAdmin = () => {
    const { loggedIn } = useContext(AuthContext);
    const [isAdmin, setIsAdmin] = useState(false);
  
    useEffect(() => {
        const fetchAdminStatus = async () => {
            try {
              const loggedInUser = loggedIn // Replace this with the code to get the logged-in user's username;
              if (loggedInUser) {
                const userId = await getUserId(loggedInUser);
                if (userId) {
                  const isAdminUser = await checkAdmin(userId);
                  setIsAdmin(isAdminUser);
                } else {
                  setIsAdmin(false); // User ID not found, consider the user as non-admin
                }
              }
            } catch (error) {
              console.error('Error checking admin status:', error);
              setIsAdmin(false); // Handle the error gracefully by considering the user as non-admin
            }
          };
      
          fetchAdminStatus();
        }, []);



  // get user based on username

  const getUserId = async (username) => {
    try {
      // Replace the URL with the endpoint that fetches the user ID based on the username
      const response = await fetch(`http://localhost:5000/api/username/${username}`);
      const data = await response.json();
      if (data && data.userId) {
        return data.userId;
      }
      return null;
    } catch (error) {
      console.error('Error fetching user ID:', error);
      return null;
    }
  };

  // check if admin based on userid

  const checkAdmin = async (userId) => {
    try {
      // Replace the URL with the endpoint that checks if the user ID is part of the admin table
      const response = await fetch(`http://localhost:5000/api/isadmin/${userId}`);
      const data = await response.json();
      if (data && data.isAdmin) {
        return true;
      }
      return false;
    } catch (error) {
      console.error('Error checking admin:', error);
      return false;
    }
  };

  return isAdmin;
};

export default useIsAdmin

