import React, { useState } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';

const Contact = () => {
  const [contact, setContact] = useState({
    firstName: "",
    lastName: "",
    email: "",
    text: "",
    phoneNumber: ""
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const response = await fetch("http://localhost:5000/api/contact", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(contact),
        });
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        const result = await response.json();
        console.log("New contact added:", result);
        setIsSubmitted(true);
      } catch (error) {
        console.error("Error:", error);
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setContact({ ...contact, [name]: value });
  };

  const validateForm = () => {
    const { firstName, lastName, email, text, phoneNumber } = contact;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const forbiddenCharsRegex = /["',`]/;

    if (!firstName.trim() || !lastName.trim() || !email.trim() || !text.trim() || !phoneNumber.trim()) {
      alert("All fields are required");
      return false;
    }
    if (!emailRegex.test(email)) {
      alert("Invalid email format");
      return false;
    }
    if (firstName.length > 50 || lastName.length > 50) {
      alert("First Name and Last Name cannot exceed 50 characters");
      return false;
    }
    if (text.length > 3000) {
      alert("Message cannot exceed 3000 characters");
      return false;
    }
    if (phoneNumber.length !== 11) {
      alert("Phone Number must be 11 digits");
      return false;
    }
    if (forbiddenCharsRegex.test(firstName) || forbiddenCharsRegex.test(lastName) || forbiddenCharsRegex.test(text)) {
      alert("Invalid characters detected. Please remove any quotes (\", ', `)");
      return false;
    }

    return true;
  };

  const handleModalClose = () => {
    setIsSubmitted(false);
    setContact({
      firstName: "",
      lastName: "",
      email: "",
      text: "",
      phoneNumber: ""
    });
  };

  return (
    <div>
      <Header />
      <form onSubmit={handleSubmit}>
        {/* Form fields go here */}
        <button type="submit">Submit</button>
      </form>
      {isSubmitted && (
        <div className="modal">
          <div className="modal-content">
            <h3>Submission Successful</h3>
            <p>Your form has been submitted successfully.</p>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Contact;
