import React, { useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../../pages/auth/Auth';
import Footer from '../../components/footer/Footer';
// import Header from '../../components/header/Header';
import { useDarkMode } from '../../pages/darkmode/DarkMode';
import CryptoJS from 'crypto-js';
import './login.css';

const Login = () => {
  const secretPass = 'XkhZG4fW2t2W';

  const [loginData, setLoginData] = useState({
    username: '',
    password: '',
  });

  const [isSubmitted, setIsSubmitted] = useState(false);

  const { login } = useContext(AuthContext); 
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const encryptedPassword = CryptoJS.AES.encrypt(loginData.password, secretPass).toString();
        const response = await fetch('http://localhost:5000/api/login', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ ...loginData, password: encryptedPassword }),
        });
  
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        //const result = await response.json();
        console.log('Logged in:', loginData.username);
        login(loginData.username);
        setIsSubmitted(true);
        setLoginData({
          username: '',
          password: '',
        });
        navigate('/');
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLoginData({ ...loginData, [name]: value });
  };

  const validateForm = () => {
    const { username, password } = loginData;

    if (!username.trim() || !password.trim()) {
      alert('Username and password are required');
      return false;
    }

    return true;
  };

  // fotgot password
    const [forgotPasswordData, setForgotPasswordData] = useState({
    username: '',
    email: '',
    phoneNumber: '',
    dob: '',
  });

  const handleForgotPasswordSubmit = async (e) => {
    e.preventDefault();
    if (validateForgotPasswordForm()) {
      try {
        const response = await fetch('http://localhost:5000/api/forgotPassword', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(forgotPasswordData),
        });
  
        if (response.ok) {
          const result = await response.json();
          if (result.success) {
            alert('Password reset link sent to your email');
          } else {
            alert('Invalid credentials. Please check your input.');
          }
        } else {
          throw new Error('Network response was not ok');
        }
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  const handleForgotPasswordChange = (e) => {
    const { name, value } = e.target;
    setForgotPasswordData({ ...forgotPasswordData, [name]: value });
  };

  const validateForgotPasswordForm = () => {
    const { username, email, phoneNumber, dob } = forgotPasswordData;

    return true; 
  };

  const { darkMode } = useDarkMode();

  return (
    <div>
      <Header/>
    <div className={`login ${darkMode ? 'dark-mode' : ''}`}>
      <h1>Login</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input type="text" name="username" value={loginData.username} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" name="password" value={loginData.password} onChange={handleChange} required />
        </div>
        <button type="submit">Login</button>
      </form>
      {isSubmitted && (
        // <div className="modal">
        //   <div className="modal-content">
       <div>
         <div >
            <h3>Login Successful</h3>
            <p>You have successfully logged in.</p>
            <button onClick={() => setIsSubmitted(false)}>Close</button>
          </div>
        </div>
      )}

<div className="forgot-password-form">
          <h2>Forgot Password</h2>
          <form onSubmit={handleForgotPasswordSubmit}>
            <div>
              <label htmlFor="username">Username:</label>
              <input
                type="text"
                name="username"
                value={forgotPasswordData.username}
                onChange={handleForgotPasswordChange}
                required
                maxLength={100}
              />
            </div>
            <div>
              <label htmlFor="email">Email:</label>
              <input
                type="email"
                name="email"
                value={forgotPasswordData.email}
                onChange={handleForgotPasswordChange}
                required
                maxLength={100}
              />
            </div>
            <div>
              <label htmlFor="phoneNumber">Phone Number:</label>
              <input
                type="tel"
                name="phoneNumber"
                value={forgotPasswordData.phoneNumber}
                onChange={handleForgotPasswordChange}
                required
                maxLength={11}
                pattern="[0-9]{11}"
              />
            </div>
            <div>
            <label htmlFor="dob">Date of Birth:</label>
            <input
              type="date"
              name="dob"
              value={forgotPasswordData.dob}
              onChange={handleForgotPasswordChange}
              required
            />
          </div>
            <button type="submit">Submit</button>
            <button type="button" onClick={() => setForgotPasswordData({})}>
              Clear
            </button>
          </form>
        </div>
    </div>
    
    <Footer/>
    </div>
  );
};

export default Login;
