import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';

const Admin = () => {
  const [content, setContent] = useState([]);

  const getData = async (url) => {
    const data = await fetch(url, {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setContent(data);
  };

  const deleteContent = async (contentId) => {
    await fetch(`http://localhost:5000/api/content/${contentId}`, {
      method: 'DELETE',
    });
    const newContent = content.filter((item) => item.ContentID !== contentId);
    setContent(newContent);
  };

  useEffect(() => {
    getData('http://localhost:5000/api/content');
  }, []);

  return (
    <div>
      <Header />
      <Navbar />

      {/* Add content */}
      {/* Delete content */}
      {/* Edit content */}
      {/* Generate QR code */}
      {/* Change preview content details */}

      {/* Table */}
      <div className="App">
        <button onClick={() => getData('http://localhost:5000/api/content')}>Get Content</button>
        <table>
          <thead>
            <tr>
              <th>Title</th>
              <th>Text Content</th>
              <th>Image Reference</th>
              <th>Image Alt</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {content.map((item) => (
              <tr key={item.ContentID}>
                <td>{item.Title}</td>
                <td>{item.textContent}</td>
                <td>{item.imgRef}</td>
                <td>{item.imgAlt}</td>
                <td>
                  <button onClick={() => deleteContent(item.ContentID)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Footer />
    </div>
  );
};

export default Admin;
