import React, { useState } from 'react';
import Footer from "../../components/footer/Footer";
import Header from "../../components/header/Header";
import Navbar from "../../components/navbar/Navbar";

const Signup = () => {
  const [imageName, setImageName] = useState('');

  const handleInputChange = (event) => {
    setImageName(event.target.value);
  };

  const imageList = {
    guitar: require('../../images/guitar.jpg').default,
    cat: require('../../images/bicy.jpg').default,
    // Add more images and their corresponding paths here
  };

  const getImageSource = (imageName) => {
    if (imageList.hasOwnProperty(imageName)) {
      return imageList[imageName];
    }
    // Return a fallback image source if the user-entered image name doesn't match
    return require('../../images/default.jpg').default;
  };

  return (
    <div>
      <Header />
      <Navbar />
      
      <div>
        <input type="text" value={imageName} onChange={handleInputChange} />
        <img src={getImageSource(imageName)} alt="User ex" />
      </div>
      
      <Footer />
    </div>
  );
};

export default Signup;
