import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";

const TextToSpeech = ({ targetSelector, voice, rate, pitch }) => {
  const [synth, setSynth] = useState(null);
  const [text, setText] = useState("");

  useEffect(() => {
    const voices = window.speechSynthesis.getVoices();
    const defaultVoice = voices.find((v) => v.name === voice);

    const synthInstance = new SpeechSynthesisUtterance();
    synthInstance.voice = defaultVoice;
    synthInstance.rate = rate;
    synthInstance.pitch = pitch;
    setSynth(synthInstance);

    return () => {
      if (synthInstance && synthInstance.speaking) {
        synthInstance.cancel();
      }
    };
  }, [voice, rate, pitch]);

  const handleSpeak = () => {
    if (synth) {
      const utterThis = new SpeechSynthesisUtterance(text);
      utterThis.voice = synth.voice;
      utterThis.rate = synth.rate;
      utterThis.pitch = synth.pitch;

      window.speechSynthesis.speak(utterThis);
    }
  };

  const handleChange = (event) => {
    setText(event.target.value);
  };

  return (
    <div>
      <input type="text" onChange={handleChange} />
      <button onClick={handleSpeak}>Speak</button>
      {targetSelector && (
        <div>
          {Array.from(document.querySelectorAll(targetSelector)).map(
            (element) => (
              <button
                key={element.textContent}
                onClick={() => {
                  setText(element.textContent);
                  handleSpeak();
                }}
              >
                Speak {element.textContent}
              </button>
            )
          )}
        </div>
      )}
    </div>
  );
};

TextToSpeech.propTypes = {
  targetSelector: PropTypes.string,
  voice: PropTypes.string,
  rate: PropTypes.number,
  pitch: PropTypes.number,
};

TextToSpeech.defaultProps = {
  targetSelector: "",
  voice: "Google UK English Female",
  rate: 1,
  pitch: 1,
};

export default TextToSpeech;
