import React, { useState } from 'react';

const api = {
    key: "92be35bf46d93081ae004cfa5359ec1d",
    base: 'http://api.openweathermap.org/data/2.5/'
};

const Weather = () => {

    const [weather, setweather]= useState({});
    const [weatherIcon, setWeatherIcon] = useState('');

    const getWeathericon = (iconCode) => {
        return `http://openweathermap.org/img/wn/${iconCode}.png`;
    };

    const weat = () => {
        fetch(`${api.base}weather?q=belfast&units=metric&APPID=${api.key}`)
        .then(res => res.json())
        .then(result => { console.log(result); setweather(result); setWeatherIcon(result.weather[0].icon); });
    };

    return (
        <div>
            <button onClick={weat}>get current weather data</button>
            
            <p>Location:{weather.name}</p>
            <p>Temperature:{weather.main && weather.main.temp} </p>
            <img src={getWeathericon(weatherIcon)} alt="Weather Icon" />
        </div>
    );
};

export default Weather;
