// export default Header

import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import './header.css';

const Navbar = () => {
  const [showMenu, setShowMenu] = useState(false);

  const handleToggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="navbar-logo">
          EVENT EIRE
        </div>
        <div className={`navbar-menu ${showMenu ? 'active' : ''}`}>
          <ul className="navbar-links">
            <li>
              <NavLink exact to="/" activeClassName="active">Home</NavLink>
            </li>
            <li>
              <NavLink to="/qrReader" activeClassName="active">QR READER</NavLink>
            </li>
            <li>
              <NavLink to="/contactUs" activeClassName="active">CONTACT US</NavLink>
            </li>
            <li>
              <NavLink to="/admin" activeClassName="active">ADMIN</NavLink>
            </li>
          </ul>
        </div>
        <div className="navbar-toggle" onClick={handleToggleMenu}>
          <span className={`toggle-icon ${showMenu ? 'active' : ''}`}></span>
        </div>
      </div>
      <div className={`navbar-login ${showMenu ? 'active' : ''}`}>
        <NavLink to="/login" activeClassName="active">Login</NavLink>
        <NavLink to="/logout" activeClassName="active">Logout</NavLink>
      </div>
    </nav>
  );
};

export default Navbar;
