import React, { useState, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../pages/auth/Auth';
import { DarkModeContext } from '../../pages/darkmode/DarkMode';
import useisAdmin from '../isAdmin/isAdmin'; 
import './header.css';
const Navbar = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const { loggedIn, logout } = useContext(AuthContext);
  const isAdminUser = useisAdmin(); 
  const { darkMode, toggleDarkMode } = useContext(DarkModeContext);

  const handleShowNavbar = () => {
    setShowNavbar(!showNavbar);
  };

  const handleLogout = () => {
    logout();
    window.location.reload();
  };

  const handleToggleDarkMode = () => {
    toggleDarkMode();
  };

  const handleNavLinkClick = () => {
    setShowNavbar(false); // Close the dropdown when a NavLink is clicked
  };

  return (
    <nav className={`navbar ${darkMode ? 'dark-mode' : ''}`}>
      <div className="container">
        <div className="logo">EVENT EIRE</div>
        <div className="menu-icon" onClick={handleShowNavbar}>
          Menu
        </div>
        <div className={`nav-elements  ${showNavbar && 'active'}`}>
          <ul>
            <li>
              <NavLink to="/" onClick={handleNavLinkClick}>
                Home
              </NavLink>
            </li>
            <li>
              <NavLink to="/qrReader" onClick={handleNavLinkClick}>
                QR READER
              </NavLink>
            </li>
            <li>
              <NavLink to="/contactUs" onClick={handleNavLinkClick}>
                CONTACT US
              </NavLink>
            </li>
            {isAdminUser && (
              <li>
                <NavLink to="/admin" onClick={handleNavLinkClick}>
                  ADMIN
                </NavLink>
              </li>
            )}
          </ul>
          <div className="login-logout">
            {loggedIn !== null ? (
              <>
                <span>logged: {loggedIn}</span>
                <NavLink to="/" onClick={handleLogout}>
                  <button>Logout</button>
                </NavLink>
              </>
            ) : (
              <>
                <NavLink to="/login">
                  <button onClick={handleNavLinkClick}>Login</button>
                </NavLink>

                <NavLink to="/signup">
                  <button onClick={handleNavLinkClick}>Signup</button>
                </NavLink>
              </>
            )}
          </div>
          <div className="dark-mode-button">
            <button onClick={handleToggleDarkMode}>Toggle Dark Mode</button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
