import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';

const Contact = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  // Tab 1 - Employees
  const [employees, setEmployees] = useState([]);

  const getEmployeesData = async () => {
    const data = await fetch('http://localhost:5000/api/employees', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setEmployees(data);
  };

  const deleteEmployee = async (id) => {
    await fetch(`http://localhost:5000/api/employees/${id}`, {
      method: 'DELETE',
    });
    const newEmployees = employees.filter((employee) => employee.EmployeeID !== id);
    setEmployees(newEmployees);
  };

  useEffect(() => {
    getEmployeesData();
  }, []);

  // Tab 2 - Users
  const [users, setUsers] = useState([]);

  const getUsersData = async () => {
    const data = await fetch('http://localhost:5000/api/users', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setUsers(data);
  };

  const deleteUser = async (userId) => {
    // Check if the user ID exists in the admin table
    const isAdmin = await fetch(`http://localhost:5000/api/admin/${userId}`, {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());

    if (isAdmin) {
      console.log('User is an admin and cannot be deleted.');
      return;
    }

    // Proceed with deleting the user from the users table
    await fetch(`http://localhost:5000/api/users/${userId}`, {
      method: 'DELETE',
    });
    
    // Update the users state by filtering out the deleted user
    const newUsers = users.filter((user) => user.userId !== userId);
    setUsers(newUsers);
  };

  useEffect(() => {
    getUsersData();
  }, []);

  //tab 3 - content
  const [content, setContent] = useState([]);

  const getContentData = async (url) => {
    const data = await fetch('http://localhost:5000/api/Content', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setContent(data);
  };

  const deleteContent = async (contentId) => {
    await fetch(`http://localhost:5000/api/Content/${contentId}`, {
      method: 'DELETE',
    });
    const newContent = content.filter((item) => item.ContentID !== contentId);
    setContent(newContent);
  };

  useEffect(() => {
    getContentData();
  }, []);

  // tab 4 preview
  const [previewContent, setPreviewContent] = useState([]);

  const getPreviewData = async (url) => {
    const data = await fetch('http://localhost:5000/api/previewContent', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setPreviewContent(data);
  };


  const deletePreview = async (previewId) => {
    await fetch(`http://localhost:5000/api/previewContent/${previewId}`, {
      method: 'DELETE',
    });
    const newPreviewContent = previewContent.filter((item) => item.previewID !== previewId);
    setPreviewContent(newPreviewContent);
  };

  useEffect(() => {
    getPreviewData();
  }, []);

  return (
    <div>
      <Header />
      <Navbar />
      <div className="tabs-container">
        <div className="tab-header">
          {/* Tab headers */}
          <div className={`tab ${activeTab === 0 ? 'active' : ''}`} onClick={() => handleTabClick(0)}>
            Tab 1 employees
          </div>
          <div className={`tab ${activeTab === 1 ? 'active' : ''}`} onClick={() => handleTabClick(1)}>
            Tab 2 - users
          </div>
          <div className={`tab ${activeTab === 2 ? 'active' : ''}`} onClick={() => handleTabClick(2)}>
            Tab 3 - content
          </div>
          <div className={`tab ${activeTab === 3 ? 'active' : ''}`} onClick={() => handleTabClick(3)}>
            Tab 4 - preview
          </div>
        </div>

        {/* Tab content */}
        <div className="tab-content">
          {/* Content for Tab 1 - Employees */}
          {activeTab === 0 && (
            <div className="App">
              <button onClick={getEmployeesData}>Get Employees</button>
              <table>
                <thead>
                  <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Age</th>
                    <th>Gender</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {employees.map((employee) => (
                    <tr key={employee.EmployeeID}>
                      <td>{employee.FirstName}</td>
                      <td>{employee.LastName}</td>
                      <td>{employee.Age}</td>
                      <td>{employee.Gender}</td>
                      <td>
                        <button onClick={() => deleteEmployee(employee.EmployeeID)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Content for Tab 2 - Users */}
          {activeTab === 1 && (
            <div className="App">
              <button onClick={getUsersData}>Get Users</button>
              <table>
                <thead>
                  <tr>
                    <th>Username</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Phone Number</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.userId}>
                      <td>{user.Username}</td>
                      <td>{user.FirstName}</td>
                      <td>{user.LastName}</td>
                      <td>{user.Email}</td>
                      <td>{user.PhoneNumber}</td>
                      <td>
                        <button onClick={() => deleteUser(user.userId)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Content for Tab 3 - Content */}
          {activeTab === 2 && (
            <div className="App">
              <button onClick={getContentData}>Get Content</button>
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {content.map((item) => (
                    <tr key={item.ContentID}>
                      <td>{item.Title}</td>
                      <td>{item.Description}</td>
                      <td>{item.Category}</td>
                      <td>
                        <button onClick={() => deleteContent(item.ContentID)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Content for Tab 4 - Preview */}
          {activeTab === 3 && (
            <div className="App">
              <button onClick={getPreviewData}>Get Preview</button>
              <table>
                <thead>
                  <tr>
                    <th>Title</th>
                    <th>Description</th>
                    <th>Category</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {previewContent.map((item) => (
                    <tr key={item.previewID}>
                      <td>{item.Title}</td>
                      <td>{item.Description}</td>
                      <td>{item.Category}</td>
                      <td>
                        <button onClick={() => deletePreview(item.previewID)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Contact;
