import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import QRCode from 'qrcode.react';
import { useDarkMode } from '../../pages/darkmode/DarkMode';
import Footer from '../../components/footer/Footer';
import './admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  ///////////////////////////////////////////////////////////////

  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);

  const getUsersData = async () => {
    const userData = await fetch('http://localhost:5000/api/users', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(userData);
    setUsers(userData);

    const adminData = await fetch('http://localhost:5000/api/admin', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(adminData);
    setAdmins(adminData);
  };

  const deleteUser = async (userId) => {
    const isAdmin = admins.some((admin) => admin.userId === userId);
    if (isAdmin) {
      alert('Cannot delete admin user.');
      return;
    }

    const confirmed = window.confirm('Are you sure you want to delete this user?');
    if (confirmed) {
      await fetch(`http://localhost:5000/api/users/${userId}`, {
        method: 'DELETE',
      });
      const newUsers = users.filter((user) => user.userId !== userId);
      setUsers(newUsers);
    }
  };

  useEffect(() => {
    getUsersData();
  }, []);

  // tab 2 /////////////////////////////////////////////////////////////////////////////
  const [content, setContent] = useState([]);

  const getContentData = async () => {
    const data = await fetch('http://localhost:5000/api/Content', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setContent(data);
  };

  const deleteContent = async (contentId) => {
    const confirmed = window.confirm('Are you sure you want to delete this content?');
    if (confirmed) {
      await fetch(`http://localhost:5000/api/Content/${contentId}`, {
        method: 'DELETE',
      });
      const newContent = content.filter((item) => item.ContentID !== contentId);
      setContent(newContent);
    }
  };

  //update form

  const [updateContentFormData, setUpdateContentFormData] = useState({
    title: '',
    textContent: '',
    imgRef: '',
    imgAlt: '',
  });

  const [selectedContentId, setSelectedContentId] = useState(null);

  const handleUpdateContentFormChange = (e) => {
    setUpdateContentFormData({
      ...updateContentFormData,
      [e.target.name]: e.target.value,
    });
  };

  const handleUpdateContentFormSubmit = async (e) => {
    e.preventDefault();

    if (
      updateContentFormData.title.includes('""') ||
      updateContentFormData.title.includes("''") ||
      updateContentFormData.title.includes("'") ||
      updateContentFormData.title.includes("`") ||
      updateContentFormData.textContent.includes('""') ||
      updateContentFormData.textContent.includes("''") ||
      updateContentFormData.textContent.includes("'") ||
      updateContentFormData.textContent.includes("`") ||
      updateContentFormData.imgRef.includes('""') ||
      updateContentFormData.imgRef.includes("''") ||
      updateContentFormData.imgRef.includes("'") ||
      updateContentFormData.imgRef.includes("`") ||
      updateContentFormData.imgAlt.includes('""') ||
      updateContentFormData.imgAlt.includes("''") ||
      updateContentFormData.imgAlt.includes("'") ||
      updateContentFormData.imgAlt.includes("`")
    ) {
      alert("Invalid input: The form data contains forbidden characters.");
      return;
    }

    const response = await fetch(`http://localhost:5000/api/Content/${selectedContentId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updateContentFormData),
    });

    if (response.ok) {
      const updatedContent = content.map((item) => {
        if (item.ContentID === selectedContentId) {
          return { ...item, ...updateContentFormData };
        }
        return item;
      });
      setContent(updatedContent);
      setUpdateContentFormData({
        title: '',
        textContent: '',
        imgRef: '',
        imgAlt: '',
      });
      setSelectedContentId(null);
    }
  };

  useEffect(() => {
    getContentData();
  }, []);

  // tab 3/////////////////////////////////////////////////////////////////////////////////////
  const [previewContent, setPreviewContent] = useState([]);

  const getPreviewData = async () => {
    const data = await fetch('http://localhost:5000/api/previewContent', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setPreviewContent(data);
  };

  const deletePreview = async (previewId) => {
    const popupConfirm = window.confirm('Are you sure you want to delete this preview?');
    if (popupConfirm) {
      await fetch(`http://localhost:5000/api/previewContent/${previewId}`, {
        method: 'DELETE',
      });
      const newPreviewContent = previewContent.filter((item) => item.previewID !== previewId);
      setPreviewContent(newPreviewContent);
    }
  };

  // update form

  const [updateFormData, setUpdateFormData] = useState({
    title: '',
    textContent: '',
    imgRef: '',
    imgAlt: '',
    type: '',
  });
  const [selectedPreviewId, setSelectedPreviewId] = useState(null);

  const handleUpdateFormChange = (e) => {
    setUpdateFormData({
      ...updateFormData,
      [e.target.name]: e.target.value,
    });
  };

  const handleUpdateFormSubmit = async (e) => {
    e.preventDefault();

    if (
      updateFormData.title.includes('""') ||
      updateFormData.title.includes("''") ||
      updateFormData.title.includes("'") ||
      updateFormData.title.includes("`") ||
      updateFormData.textContent.includes('""') ||
      updateFormData.textContent.includes("''") ||
      updateFormData.textContent.includes("'") ||
      updateFormData.textContent.includes("`") ||
      updateFormData.imgRef.includes('""') ||
      updateFormData.imgRef.includes("''") ||
      updateFormData.imgRef.includes("'") ||
      updateFormData.imgRef.includes("`") ||
      updateFormData.imgAlt.includes('""') ||
      updateFormData.imgAlt.includes("''") ||
      updateFormData.imgAlt.includes("'") ||
      updateFormData.imgAlt.includes("`")
    ) {
      alert("Invalid input: The form data contains forbidden characters.");
      return;
    }

    const response = await fetch(`http://localhost:5000/api/previewContent/${selectedPreviewId}`, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(updateFormData),
    });

    if (response.ok) {
      const updatedPreviewContent = previewContent.map((item) => {
        if (item.previewID === selectedPreviewId) {
          return { ...item, ...updateFormData };
        }
        return item;
      });
      setPreviewContent(updatedPreviewContent);
      setUpdateFormData({
        title: '',
        textContent: '',
        imgRef: '',
        imgAlt: '',
        type: '',
      });
      setSelectedPreviewId(null);
    }
  };

  useEffect(() => {
    getPreviewData();
  }, []);

  // ... (Previous code)

  return (
    <>
      <Header />
      <div className="qr_code">
        <QRCode value="https://www.example.com" />
      </div>
      <div className="admin_wrapper">
        <div className="admin_title">
          <h1>Admin Panel</h1>
        </div>
        <div className="admin_menu">
          <div
            className={`admin_menu_item ${activeTab === 0 ? 'active' : ''}`}
            onClick={() => handleTabClick(0)}
          >
            Users
          </div>
          <div
            className={`admin_menu_item ${activeTab === 1 ? 'active' : ''}`}
            onClick={() => handleTabClick(1)}
          >
            Content
          </div>
          <div
            className={`admin_menu_item ${activeTab === 2 ? 'active' : ''}`}
            onClick={() => handleTabClick(2)}
          >
            Preview Content
          </div>
        </div>
        <div className="admin_content">
          {activeTab === 0 && (
            <div className="admin_users">
              <h2>Users</h2>
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Username</th>
                    <th>Email</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.userId}>
                      <td>{user.userId}</td>
                      <td>{user.username}</td>
                      <td>{user.email}</td>
                      <td>
                        <button onClick={() => deleteUser(user.userId)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
          {activeTab === 1 && (
            <div className="admin_content_tab">
              <h2>Content</h2>
              <button onClick={showUpdateContentPopup}>Update Content</button>
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Text Content</th>
                    <th>Image Reference</th>
                    <th>Image Alt</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {content.map((item) => (
                    <tr key={item.ContentID}>
                      <td>{item.ContentID}</td>
                      <td>{item.title}</td>
                      <td>{item.textContent}</td>
                      <td>{item.imgRef}</td>
                      <td>{item.imgAlt}</td>
                      <td>
                        <button onClick={() => deleteContent(item.ContentID)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
          {activeTab === 2 && (
            <div className="admin_content_tab">
              <h2>Preview Content</h2>
              <button onClick={showUpdatePreviewPopup}>Update Preview</button>
              <table>
                <thead>
                  <tr>
                    <th>ID</th>
                    <th>Title</th>
                    <th>Text Content</th>
                    <th>Image Reference</th>
                    <th>Image Alt</th>
                    <th>Type</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {previewContent.map((item) => (
                    <tr key={item.previewID}>
                      <td>{item.previewID}</td>
                      <td>{item.title}</td>
                      <td>{item.textContent}</td>
                      <td>{item.imgRef}</td>
                      <td>{item.imgAlt}</td>
                      <td>{item.type}</td>
                      <td>
                        <button onClick={() => deletePreview(item.previewID)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Update Content Form Pop-up */}
      {showUpdateContentForm && (
        <div className="popup">
          <div className="popup_inner">
            <h2>Update Content</h2>
            <form onSubmit={handleUpdateContentFormSubmit}>
              <div>
                <label>Title:</label>
                <input
                  type="text"
                  name="title"
                  value={updateContentFormData.title}
                  onChange={handleUpdateContentFormChange}
                />
              </div>
              <div>
                <label>Text Content:</label>
                <input
                  type="text"
                  name="textContent"
                  value={updateContentFormData.textContent}
                  onChange={handleUpdateContentFormChange}
                />
              </div>
              <div>
                <label>Image Reference:</label>
                <input
                  type="text"
                  name="imgRef"
                  value={updateContentFormData.imgRef}
                  onChange={handleUpdateContentFormChange}
                />
              </div>
              <div>
                <label>Image Alt:</label>
                <input
                  type="text"
                  name="imgAlt"
                  value={updateContentFormData.imgAlt}
                  onChange={handleUpdateContentFormChange}
                />
              </div>
              <div>
                <button type="submit">Update</button>
                <button onClick={hideUpdateContentPopup}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Update Preview Form Pop-up */}
      {showUpdatePreviewForm && (
        <div className="popup">
          <div className="popup_inner">
            <h2>Update Preview Content</h2>
            <form onSubmit={handleUpdateFormSubmit}>
              <div>
                <label>Title:</label>
                <input
                  type="text"
                  name="title"
                  value={updateFormData.title}
                  onChange={handleUpdateFormChange}
                />
              </div>
              <div>
                <label>Text Content:</label>
                <input
                  type="text"
                  name="textContent"
                  value={updateFormData.textContent}
                  onChange={handleUpdateFormChange}
                />
              </div>
              <div>
                <label>Image Reference:</label>
                <input
                  type="text"
                  name="imgRef"
                  value={updateFormData.imgRef}
                  onChange={handleUpdateFormChange}
                />
              </div>
              <div>
                <label>Image Alt:</label>
                <input
                  type="text"
                  name="imgAlt"
                  value={updateFormData.imgAlt}
                  onChange={handleUpdateFormChange}
                />
              </div>
              <div>
                <label>Type:</label>
                <input
                  type="text"
                  name="type"
                  value={updateFormData.type}
                  onChange={handleUpdateFormChange}
                />
              </div>
              <div>
                <button type="submit">Update</button>
                <button onClick={hideUpdatePreviewPopup}>Cancel</button>
              </div>
            </form>
          </div>
        </div>
      )}

      <Footer />
    </>
  );
};

export default Admin;
