import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';
import { useDarkMode } from '../../pages/darkmode/DarkMode';
import FontSizeChanger from '../../components/fontSizeChanger/FontSizeChanger';
import TextToSpeech from '../../components/textToSpeech/TextToSpeech';
import './home.css';

const Home = () => {
  const [wellCurrent, setWellCurrent] = useState('');
  const [wellFuture, setWellFuture] = useState('');
  const [previewContent, setPreviewContent] = useState({ current: null, future: null });
  const [loggedInUser, setLoggedInUser] = useState('');

  useEffect(() => {
    const user = localStorage.getItem('loggedInUser');
    setLoggedInUser(user || '');
  }, []);

  const getImagePath = () => {
    const imagePath = previewContent.current ? require(`../../images/${previewContent.current.imgRef}.jpg`) : null;
    return imagePath;
  };

  const getImagePath2 = () => {
    const imagePath = previewContent.future ? require(`../../images/${previewContent.future.imgRef}.jpg`) : null;
    return imagePath;
  };

  const targetId = 'targetId';

  useEffect(() => {
    const targetElement = document.getElementById(targetId);
    if (targetElement) {
      setWellCurrent(targetElement.innerText);
    }
  }, [targetId]);

  useEffect(() => {
    const fetchPreviewContent = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/previewContent');
        const data = await response.json();
        setPreviewContent({
          current: data.find((content) => content.type === 'current'),
          future: data.find((content) => content.type === 'future'),
        });
      } catch (error) {
        console.log('Error fetching previewContent data', error);
      }
    };

    fetchPreviewContent();
  }, []);

  useEffect(() => {
    setWellCurrent(document.getElementById(targetId)?.innerText || '');
  }, [previewContent]);

  useEffect(() => {
    setWellFuture(previewContent.future?.textContent || '');
  }, [previewContent]);

  const { darkMode } = useDarkMode();

  return (
    <div>
      <div className={`home ${darkMode ? 'dark-mode' : ''}`}>
        <Header />
        <div className="overview">
          <div className="sizeChanger">
            <FontSizeChanger initialFontSize={16} targetSelector=".home" />
          </div>
          <div>
            <h1>Home</h1>
          </div>
          <div>{loggedInUser && <p>Welcome, {loggedInUser}</p>}</div>
          <h3>
            EventEire is a newly established company with the aims to enable everyone the
            chance to visit and enjoy different events, shows and galleries to their fullest
            potential in which everyone can roam freely and experience the different kinds of art
            and history in an interactive and fun way.
          </h3>
        </div>

        <div className="content-container">
          <div className="eventsWrapper">
            <div className="currentCont">
              <h4>Currently on show</h4>
              {previewContent.current && (
                <>
                  <div>{previewContent.current.Title}</div>
                  <img src={getImagePath()} alt={previewContent.current.imgAlt} />
                  <div>{previewContent.current.textContent}</div>
                  <TextToSpeech text={previewContent.current.textContent} />
                </>
              )}
            </div>

            <div className="futurecont">
              <h4>What's to come in the future</h4>
              {previewContent.future && (
                <>
                  <div>{previewContent.future.Title}</div>
                  <img src={getImagePath2()} alt={previewContent.future.imgAlt} />
                  <div id={targetId}>{previewContent.future.textContent}</div>
                  <TextToSpeech text={wellFuture} />
                </>
              )}
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Home;
