import React, { createContext, useEffect, useState, useContext } from 'react';

export const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const loggedInStorage = window.localStorage.getItem('loggedInUser');
  const darkModeStorage = window.localStorage.getItem('darkMode');

  const [loggedIn, setLoggedIn] = useState(loggedInStorage || null);
  const [darkMode, setDarkMode] = useState(darkModeStorage === 'true');

  useEffect(() => {
    window.localStorage.setItem('loggedInUser', loggedIn);
  }, [loggedIn]);

  useEffect(() => {
    window.localStorage.setItem('darkMode', darkMode.toString());
  }, [darkMode]);

  const login = (username) => {
    setLoggedIn(username);
  };

  const logout = () => {
    setLoggedIn(null);
    window.localStorage.removeItem('loggedInUser');
  };

  const toggleDarkMode = () => {
    setDarkMode((prevDarkMode) => !prevDarkMode);
  };

  return (
    <AuthContext.Provider value={{ loggedIn, login, logout, darkMode, toggleDarkMode }}>
      {children}
    </AuthContext.Provider>
  );
};

export const useAuth = () => useContext(AuthContext);

export default AuthProvider;
