import React, { useState } from 'react';
import Footer from '../../components/footer/Footer';
import Header from '../../components/header/Header';
import { useDarkMode } from '../../pages/darkmode/DarkMode';
import './signup.css';
import CryptoJS from 'crypto-js';
const secretPass = "XkhZG4fW2t2W";

const Signup = () => {
  const [signupData, setSignupData] = useState({
    username: '',
    password: '',
    firstName: '',
    lastName: '',
    email: '',
    dobYear: '',
    dobMonth: '',
    dobDay: '',
    phoneNumber: '',
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

 
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validateForm()) {
      return;
    }
  
    try {
      // Fetch encrypted user data from the server
      const response = await fetch('http://localhost:5000/api/users', {
        method: 'GET',
        headers: { 'Content-Type': 'application/json' },
      });
  
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
  
      const encryptedUsers = await response.json();
  
      // Decrypt usernames and check if entered username already exists
      const decryptedUsernames = encryptedUsers.map((user) => {
        return CryptoJS.AES.decrypt(user.Username, secretPass).toString(CryptoJS.enc.Utf8);
      });
  
      if (decryptedUsernames.includes(signupData.username)) {
        alert('Username already exists');
        return;
      }

      const decryptedPhoneNumbers = encryptedUsers.map((user) => {
        return CryptoJS.AES.decrypt(user.PhoneNumber, secretPass).toString(CryptoJS.enc.Utf8);
      });
      
      if (decryptedPhoneNumbers.includes(signupData.phoneNumber)) {
        alert('Phone number already exists');
        return;
      }
      // Proceed with registration if username is unique
      const dob = `${signupData.dobYear}-${signupData.dobMonth.padStart(2, '0')}-${signupData.dobDay.padStart(2, '0')}`;
  
      const registrationResponse = await fetch('http://localhost:5000/api/users', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ ...signupData, dob }),
      });
  
      if (!registrationResponse.ok) {
        throw new Error('Network response was not ok');
      }
  
      const result = await registrationResponse.json();
      console.log('New user registered:', result);
      setIsSubmitted(true);
      setSignupData({
        username: '',
        password: '',
        firstName: '',
        lastName: '',
        email: '',
        dobYear: '',
        dobMonth: '',
        dobDay: '',
        phoneNumber: '',
      });
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSignupData({ ...signupData, [name]: value });
  };

  const validateForm = () => {
    const { username, password, firstName, lastName, email, dobYear, dobMonth, dobDay, phoneNumber } = signupData;
    const emailReg = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const forbiddenChars = /["',`]/;
    const yearReg = /^\d{4}$/;
    const monthReg = /^(0?[1-9]|1[0-2])$/;
    const dayReg = /^(0?[1-9]|1[0-9]|2[0-9]|3[0-1])$/;
    const maxlength = 50;

    if (
      !username.trim() ||
      !password.trim() ||
      !firstName.trim() ||
      !lastName.trim() ||
      !email.trim() ||
      !dobYear.trim() ||
      !dobMonth.trim() ||
      !dobDay.trim() ||
      !phoneNumber.trim()
    ) {
      alert('All fields are required');
      return false;
    }

    if (!emailReg.test(email)) {
      alert('Invalid email format');
      return false;
    }

    if (forbiddenChars.test(username)) {
      alert('Username cannot contain quotes or backticks');
      return false;
    }

    if (!yearReg.test(dobYear)) {
      alert('Invalid year format. Please use yyyy');
      return false;
    }

    if (!monthReg.test(dobMonth)) {
      alert('Invalid month format. Please use mm');
      return false;
    }

    if (!dayReg.test(dobDay)) {
      alert('Invalid day format. Please use dd');
      return false;
    }

    if (
      !username.trim() || username.length > maxlength ||
      !password.trim() || password.length > maxlength ||
      !firstName.trim() || firstName.length > maxlength ||
      !lastName.trim() || lastName.length > maxlength ||
      !email.trim() || email.length > maxlength ||
      !dobYear.trim() || dobYear.length !== 4 ||
      !dobMonth.trim() || dobMonth.length > 2 ||
      !dobDay.trim() || dobDay.length > 2 ||
      !phoneNumber.trim() || phoneNumber.length > 11
    ) {
      alert('Invalid input or field length exceeded');
      return false;
    }

    const dob = `${dobYear}-${dobMonth.padStart(2, '0')}-${dobDay.padStart(2, '0')}`;
    setSignupData({ ...signupData, dob });

    return true;
  };

  const handleModalClose = () => {
    setIsSubmitted(false);
  };

  const { darkMode } = useDarkMode();

  return (
    <div>
      <Header />
      <div className={`signup ${darkMode ? 'dark-mode' : ''}`}>
      <h1>Sign Up</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input type="text" name="username" value={signupData.username} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" name="password" value={signupData.password} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="firstName">First Name:</label>
          <input type="text" name="firstName" value={signupData.firstName} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="lastName">Last Name:</label>
          <input type="text" name="lastName" value={signupData.lastName} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" name="email" value={signupData.email} onChange={handleChange} required />
        </div>
        <br/>
        <div> DOB :</div>
        <div>
          <label htmlFor="dobYear">Year (yyyy):</label>
          <input type="text" name="dobYear" value={signupData.dobYear} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="dobMonth">Month (mm):</label>
          <input type="text" name="dobMonth" value={signupData.dobMonth} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="dobDay">Day (dd):</label>
          <input type="text" name="dobDay" value={signupData.dobDay} onChange={handleChange} required />
        </div>
        <br/>
        <div>
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="tel"
            name="phoneNumber"
            pattern="[0-9]{11}"
            value={signupData.phoneNumber}
            onChange={handleChange}
            required
          />
          <small>(Enter 11-digit phone number without spaces or dashes)</small>
        </div>
        <button type="submit">Register</button>
      </form>
      {isSubmitted && (
        <div className="modal">
          <div className="modal-content">
            <h3>Registration Successful</h3>
            <p>You have successfully registered.</p>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
      </div>
      <Footer />
    </div>
    
  );
};

export default Signup;
