import React, { useState, useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../pages/auth/Auth';
import useisAdmin from '../isAdmin/isAdmin'; 
import { DarkModeContext } from '../../pages/darkmode/DarkMode';
import './header.css';

const Header = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const { loggedIn, logout } = useContext(AuthContext);
  const isAdminUser = useisAdmin(); 
  const { darkMode, toggleDarkMode } = useContext(DarkModeContext);
  const [selectedLink, setSelectedLink] = useState(0);

  const handleShowNavbar = () => {
    setShowNavbar(!showNavbar);
  };

  const handleLogout = () => {
    logout();
    window.location.reload();
  };

  const handleToggleDarkMode = () => {
    toggleDarkMode();
  };

  useEffect(() => {
    const handleKeydown = (event) => {
      switch (event.key) {
        case 'ArrowLeft':
          setSelectedLink((prevLink) => (prevLink > 0 ? prevLink - 1 : 0));
          break;
        case 'ArrowRight':
          setSelectedLink((prevLink) => (prevLink < 4 ? prevLink + 1 : 4)); // Adjust this according to the number of links
          break;
        case 'Enter':
          // Get the selected link and execute the navigation
          const links = document.querySelectorAll('.nav-elements ul a');
          if (links[selectedLink]) {
            links[selectedLink].click();
          }
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeydown);

    return () => {
      window.removeEventListener('keydown', handleKeydown);
    };
  }, [selectedLink]);

  

  return (
    <div className={`navbar ${darkMode ? 'dark-mode' : ''}`}>
        <div className="logo"><h2>EVENT EIRE</h2></div>
        <div className="menu-icon" onClick={handleShowNavbar}>
          Menu
        </div>
        <div className={`nav-elements  ${showNavbar && 'active'}`}>
            <ul>
              <li>
                <NavLink to="/" className={selectedLink === 0 ? 'active' : ''}>Home</NavLink>
              </li>
              <li>
                <NavLink to="/qrReader" className={selectedLink === 1 ? 'active' : ''}>QR READER</NavLink>
              </li>
              <li>
                <NavLink to="/contactUs" className={selectedLink === 2 ? 'active' : ''}>CONTACT US</NavLink>
              </li>
              {isAdminUser && (
                <li>
                  <NavLink to="/admin" className={selectedLink === 3 ? 'active' : ''}>ADMIN</NavLink>
                </li>
              )}
            </ul>
            <div className="dark-mode-button">
              <button ref={toggleButtonRef}onClick={handleToggleDarkMode}  className={selectedLink === 4 ? 'active' : ''}>Toggle Dark Mode</button>
            </div>

            <div className="login-logout">
              {loggedIn !== null ? (
                <>
                  <span>logged: {loggedIn}</span>
                  <NavLink to="/">
                    <button onClick={handleLogout}>Logout</button>
                  </NavLink>
                </>
              ) : (
                <>
                  <NavLink to="/login">
                    <button>Login</button>
                  </NavLink>

                  <NavLink to="/signup">
                    <button>Signup</button>
                  </NavLink>
                </>
              )}
            </div>
        </div>
    </div>// navbar
  );
};

export default Header;
