import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';
import './admin.css';

const Admin = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  return (
    <div className='background'>
      <Navbar/>
    <div className="tabs-container">
      <div className="tab-header">
        {/* Tab headers */}
        <div
          className={`tab ${activeTab === 0 ? 'active' : ''}`} onClick={() => handleTabClick(0)}
        >
          Users
        </div>
        <div
          className={`tab ${activeTab === 1 ? 'active' : ''}`}onClick={() => handleTabClick(1)}
        >
          Content
        </div>
        <div
          className={`tab ${activeTab === 2 ? 'active' : ''}`} onClick={() => handleTabClick(2)}
        >
          Previews
        </div>
        <div
          className={`tab ${activeTab === 3 ? 'active' : ''}`} onClick={() => handleTabClick(2)}
        >
          Contact us
        </div>

      </div>

      {/* Tab content */}
      <div className="tab-content">
        {/* Content for Tab 1 */}
        {activeTab === 0 && 
            <div className="content">
              <button onClick={getUsersData}>Get Users</button>
              <table>
                <thead>
                  <tr>
                    <th>Username</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Phone Number</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.userId}>
                      <td>{user.Username}</td>
                      <td>{user.FirstName}</td>
                      <td>{user.LastName}</td>
                      <td>{user.Email}</td>
                      <td>{user.PhoneNumber}</td>
                      <td>
                        {user.isAdmin ? (
                          <button disabled>Delete</button>
                        ) : (
                          <button onClick={() => deleteUser(user.userId)}>Delete</button>
                        )}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            
            
            
            </div>}


        {/* Content for Tab 2 */}
        {activeTab === 1 && 
            <div className="content">
              Content for Tab 2
            

            
            </div>}



        {/* Content for Tab 3 */}
        {activeTab === 2 && 
            <div className="content">
              Content for Tab 3


            </div>}




         {/* Content for Tab 4 */}
         {activeTab === 3 && 
            <div className="content">
                Content for Tab 4


            </div>}


      </div>
    </div>


      fvfg
    <Footer/>
    </div>
  );
};

export default Admin;
