import React, { useState, useEffect } from 'react';

const TextToSpeech = ({ text }) => {
  const [pitch, setPitch] = useState(1);
  const [rate, setRate] = useState(1);
  const [voices, setVoices] = useState([]);

  const handlePitchChange = (event) => {
    setPitch(event.target.value);
  };

  const handleRateChange = (event) => {
    setRate(event.target.value);
  };

  const handleVoiceChange = (event) => {
    const selectedVoiceURI = event.target.value;
    speak(selectedVoiceURI);
  };

  const handleSpeak = () => {
    if (text && speechSynthesis.speaking) {
      speechSynthesis.cancel();
    } else if (text) {
      speak();
    }
  };

  const speak = (voiceURI = null) => {
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.pitch = pitch;
    utterance.rate = rate;
    if (voiceURI) {
      utterance.voiceURI = voiceURI;
    }
    speechSynthesis.speak(utterance);
  };

  useEffect(() => {
    const handleVoicesChanged = () => {
      const voices = speechSynthesis.getVoices();
      setVoices(voices);
    };

    speechSynthesis.addEventListener('voiceschanged', handleVoicesChanged);

    // Fetch the initial voices
    const voices = speechSynthesis.getVoices();
    setVoices(voices);

    return () => {
      speechSynthesis.removeEventListener('voiceschanged', handleVoicesChanged);
    };
  }, []);

  return (
    <div>
      <div>
        <label htmlFor="pitch">Pitch:</label>
        <input
          type="range"
          id="pitch"
          min="0.5"
          max="2"
          step="0.1"
          value={pitch}
          onChange={handlePitchChange}
        />
        <span>{pitch}</span>
      </div>
      <div>
        <label htmlFor="rate">Speed:</label>
        <input
          type="range"
          id="rate"
          min="0.5"
          max="2"
          step="0.1"
          value={rate}
          onChange={handleRateChange}
        />
        <span>{rate}</span>
      </div>
      <div>
        <label htmlFor="voice">Voice:</label>
        <select id="voice" onChange={handleVoiceChange}>
          {voices.map((voice) => (
            <option key={voice.voiceURI} value={voice.voiceURI}>
              {`${voice.name} (${voice.lang})`}
            </option>
          ))}
        </select>
      </div>
      <button onClick={handleSpeak}>{speechSynthesis.speaking ? 'Stop' : 'Speak'}</button>
    </div>
  );
};

export default TextToSpeech;
