import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';

const Admin = () => {
  const [users, setUsers] = useState([]);

  const getData = async (url) => {
    const data = await fetch(url, {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setUsers(data);
  };

  const deleteUser = async (userId) => {
    await fetch(`http://localhost:5000/api/users/${userId}`, {
      method: 'DELETE',
    });
    const newUsers = users.filter((user) => user.userId !== userId);
    setUsers(newUsers);
  };

  useEffect(() => {
    getData('http://localhost:5000/api/users');
  }, []);

  return (
    <div>
      <Header />
      <Navbar />

      {/* Add user */}
      {/* Delete user */}
      {/* Add admin */}
      {/* Delete admin user */}
      {/* Change user details */}
      {/* Change admin details */}
      {/* Generate QR code */}
      {/* Change content details */}
      {/* Change preview content details */}

      {/* Table */}
      <div className="App">
        <button onClick={() => getData('http://localhost:5000/api/users')}>Get Users</button>
        <table>
          <thead>
            <tr>
              <th>Username</th>
              <th>First Name</th>
              <th>Last Name</th>
              <th>Email</th>
              <th>Phone Number</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {users.map((user) => (
              <tr key={user.userId}>
                <td>{user.Username}</td>
                <td>{user.FirstName}</td>
                <td>{user.LastName}</td>
                <td>{user.Email}</td>
                <td>{user.PhoneNumber}</td>
                <td>
                  <button onClick={() => deleteUser(user.userId)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <Footer />
    </div>
  );
};

export default Admin;
