import React, { useState } from 'react';
import Footer from "../../components/footer/Footer";
import Header from "../../components/header/Header";
import Navbar from "../../components/navbar/Navbar";

const Signup = () => {
  const [imageName, setImageName] = useState('');

  const handleInputChange = (event) => {
    setImageName(event.target.value);
  };

  const getImageSource = (imageName) => {
    try {
      return require(`../../images/${imageName}`).default;
    } catch (error) {
      // Return a fallback image path if the image is not found
      return require('../../images/HouseOfCards.jpg').default;
    }
  };

  return (
    <div>
      <Header />
      <Navbar />
      
      <div>
        <input type="text" value={imageName} onChange={handleInputChange} />
        <img src={getImageSource(imageName)} alt="User ex" />
      </div>
     
      <Footer />
    </div>
  );
};

export default Signup;
