import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';

const Contact = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  // Tab 1 - Employees
  const [employees, setEmployees] = useState([]);

  const getEmployeesData = async () => {
    const data = await fetch('http://localhost:5000/api/employees', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setEmployees(data);
  };

  const deleteEmployee = async (id) => {
    await fetch(`http://localhost:5000/api/employees/${id}`, {
      method: 'DELETE',
    });
    const newEmployees = employees.filter((employee) => employee.EmployeeID !== id);
    setEmployees(newEmployees);
  };

  useEffect(() => {
    getEmployeesData();
  }, []);

  // Tab 2 - Users
  const [users, setUsers] = useState([]);

  const getUsersData = async () => {
    const data = await fetch('http://localhost:5000/api/users', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setUsers(data);
  };

  const deleteUser = async (userId) => {
    await fetch(`http://localhost:5000/api/users/${userId}`, {
      method: 'DELETE',
    });
    const newUsers = users.filter((user) => user.userId !== userId);
    setUsers(newUsers);
  };

  useEffect(() => {
    getUsersData();
  }, []);

  return (
    <div>
      <Header />
      <Navbar />
      <div className="tabs-container">
        <div className="tab-header">
          {/* Tab headers */}
          <div className={`tab ${activeTab === 0 ? 'active' : ''}`} onClick={() => handleTabClick(0)}>
            Tab 1
          </div>
          <div className={`tab ${activeTab === 1 ? 'active' : ''}`} onClick={() => handleTabClick(1)}>
            Tab 2
          </div>
          <div className={`tab ${activeTab === 2 ? 'active' : ''}`} onClick={() => handleTabClick(2)}>
            Tab 3
          </div>
          
        </div>

        {/* Tab content */}
        <div className="tab-content">           
          {/* Content for Tab 1 - Employees */}
          {activeTab === 0 && (
            <div className="App">
              <button onClick={getEmployeesData}>Get Employees</button>
              <table>
                <thead>
                  <tr>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Age</th>
                    <th>Gender</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {employees.map((employee) => (
                    <tr key={employee.EmployeeID}>
                      <td>{employee.FirstName}</td>
                      <td>{employee.LastName}</td>
                      <td>{employee.Age}</td>
                      <td>{employee.Gender}</td>
                      <td>
                        <button onClick={() => deleteEmployee(employee.EmployeeID)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Content for Tab 2 - Users */}
          {activeTab === 1 && (
            <div className="App">
              <button onClick={getUsersData}>Get Users</button>
              <table>
                <thead>
                  <tr>
                    <th>Username</th>
                    <th>First Name</th>
                    <th>Last Name</th>
                    <th>Email</th>
                    <th>Phone Number</th>
                    <th>Action</th>
                  </tr>
                </thead>
                <tbody>
                  {users.map((user) => (
                    <tr key={user.userId}>
                      <td>{user.Username}</td>
                      <td>{user.FirstName}</td>
                      <td>{user.LastName}</td>
                      <td>{user.Email}</td>
                      <td>{user.PhoneNumber}</td>
                      <td>
                        <button onClick={() => deleteUser(user.userId)}>Delete</button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          {/* Content for Tab 3 */}
          {activeTab === 2 && <div>Content for Tab 3</div>}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Contact;
