import React, { useState } from 'react';
import Footer from '../../components/footer/Footer';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';

const Signup = () => {
  const [signupData, setSignupData] = useState({
    username: '',
    password: '',
    firstName: '',
    lastName: '',
    email: '',
    dob: '',
    phoneNumber: '',
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      try {
        const response = await fetch('http://localhost:5000/api/signup', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(signupData),
        });
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const result = await response.json();
        console.log('New user registered:', result);
        setIsSubmitted(true);
        setSignupData({
          username: '',
          password: '',
          firstName: '',
          lastName: '',
          email: '',
          dob: '',
          phoneNumber: '',
        });
      } catch (error) {
        console.error('Error:', error);
      }
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setSignupData({ ...signupData, [name]: value });
  };

  const validateForm = () => {
    const { username, password, firstName, lastName, email, dob, phoneNumber } = signupData;
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const forbiddenCharsRegex = /["',`]/;

    if (
      !username.trim() ||
      !password.trim() ||
      !firstName.trim() ||
      !lastName.trim() ||
      !email.trim() ||
      !dob.trim() ||
      !phoneNumber.trim()
    ) {
      alert('All fields are required');
      return false;
    }
    const currentDate = new Date();
    const minimumDate = new Date();
    minimumDate.setFullYear(currentDate.getFullYear() - 16); // Minimum age of 16
    const userDateOfBirth = new Date(dob);
    
    if (userDateOfBirth > currentDate) {
      alert('Date of birth cannot be in the future');
      return false;
    }
  
    if (userDateOfBirth < minimumDate) {
      alert('You must be at least 16 years old');
      return false;
    }
  

    return true;
  };

  const handleModalClose = () => {
    setIsSubmitted(false);
  };

  return (
    <div>
      <Header />
      <Navbar />
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="username">Username:</label>
          <input type="text" name="username" value={signupData.username} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="password">Password:</label>
          <input type="password" name="password" value={signupData.password} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="firstName">First Name:</label>
          <input type="text" name="firstName" value={signupData.firstName} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="lastName">Last Name:</label>
          <input type="text" name="lastName" value={signupData.lastName} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="email">Email:</label>
          <input type="email" name="email" value={signupData.email} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="dob">Date of Birth:</label>
          <input type="date" name="dob" value={signupData.dob} onChange={handleChange} required />
        </div>
        <div>
          <label htmlFor="phoneNumber">Phone Number:</label>
          <input
            type="tel"
            name="phoneNumber"
            pattern="[0-9]{11}"
            value={signupData.phoneNumber}
            onChange={handleChange}
            required
          />
          <small>(Enter 11-digit phone number without spaces or dashes)</small>
        </div>
        <button type="submit">Register</button>
      </form>
      {isSubmitted && (
        <div className="modal">
          <div className="modal-content">
            <h3>Registration Successful</h3>
            <p>You have successfully registered.</p>
            <button onClick={handleModalClose}>Close</button>
          </div>
        </div>
      )}
      <Footer />
    </div>
  );
};

export default Signup;
