// export default Header

import React, { useState, useContext } from 'react';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../pages/auth/Auth';
import './header.css';

const Navbar = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const { loggedIn } = useContext(AuthContext); // Access the loggedIn value from the AuthContext

  const handleShowNavbar = () => {
    setShowNavbar(!showNavbar);
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="logo">EVENT EIRE</div>
        <div className="menu-icon" onClick={handleShowNavbar}>
          Menu
        </div>
        <div className={`nav-elements  ${showNavbar && 'active'}`}>
          <ul>
            <li>
              <NavLink to="/">Home</NavLink>
            </li>
            <li>
              <NavLink to="/qrReader">QR READER</NavLink>
            </li>
            <li>
              <NavLink to="/contactUs">CONTACT US</NavLink>
            </li>
            <li>
              <NavLink to="/admin">ADMIN</NavLink>
            </li>
          </ul>
          <div className="login-logout">
            {loggedIn ? ( // If user is logged in, display username and Logout button
              <>
                <span>{loggedIn.username}</span> {/* Display the username */}
                <NavLink to="/logout">
                  <button>Logout</button>
                </NavLink>
              </>
            ) : ( // If user is not logged in, display Login and Signup buttons
              <>
                <NavLink to="/login">
                  <button>Login</button>
                </NavLink>
                <NavLink to="/signup">
                  <button>Signup</button>
                </NavLink>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
