const CryptoJS = require('crypto-js');

const ENCRYPTION_KEY = 'thisIsMySecretPassphrase'; // Replace this with the same encryption key used on the server

const decrypt = (encryptedData) => {
  try {
    if (!encryptedData) {
      console.error('Encrypted data is empty');
      return null;
    }

    console.log('Encrypted data:', encryptedData); // Add this debug statement

    // Split the IV and encrypted data from the combined string
    const [ivHex, encryptedHex] = encryptedData.split(':');
    const iv = CryptoJS.enc.Hex.parse(ivHex);
    const encrypted = CryptoJS.enc.Hex.parse(encryptedHex);

    // Decrypt using the fixed IV and the encryption key
    const decryptedData = CryptoJS.AES.decrypt({ ciphertext: encrypted }, ENCRYPTION_KEY, { iv: iv }).toString(CryptoJS.enc.Utf8);

    console.log('Decrypted data:', decryptedData); // Add this debug statement

    return decryptedData;
  } catch (error) {
    console.error('Error decrypting data:', error);
    return null;
  }
};

export default decrypt;
