import Footer from "../../components/footer/Footer";
import Header from "../../components/header/Header";
import Navbar from "../../components/navbar/Navbar";
import check from "../../images/guitar.jpg";
//import userImage from '../../images/' + imageName + '.jpg';

const Signup = () => {
  const [imageName, setImageName] = useState('');

  const handleInputChange = (event) => {
    setImageName(event.target.value);
  };

  const getImagePath = () => {
    if (imageName) {
      try {
        const imagePath = require('../../images/${imageName}.jpg').default;
        return imagePath;
      } catch (error) {
        console.error(error);
      }
    }
    return null;
  };

  return (
    <div>
      <Header />
      <Navbar />
      
      <div>
        <input type="text" value={imageName} onChange={handleInputChange} />
        {imageName && <img src={getImagePath()} alt="User ex" />}
      </div>
      
      <img src={require('../../images/guitar.jpg')} alt="User ex" />
      <img src={userImage} alt="User ex" />;
     
      <Footer />
    </div>
  );
};

export default Signup;
