import React, { useState, useEffect } from 'react';
import Header from '../../components/header/Header';
import Navbar from '../../components/navbar/Navbar';
import Footer from '../../components/footer/Footer';

const Contact = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  // Tab 1 - Employees
  const [employees, setEmployees] = useState([]);

  const getEmployeesData = async () => {
    const data = await fetch('http://localhost:5000/api/employees', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setEmployees(data);
  };

  const deleteEmployee = async (id) => {
    // ...
  };

  useEffect(() => {
    getEmployeesData();
  }, []);

  // Tab 2 - Users
  const [users, setUsers] = useState([]);
  const [admins, setAdmins] = useState([]);

  const getUsersData = async () => {
    const userData = await fetch('http://localhost:5000/api/users', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(userData);
    setUsers(userData);

    const adminData = await fetch('http://localhost:5000/api/admin', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(adminData);
    setAdmins(adminData);
  };

  const deleteUser = async (userId) => {
    // ...
  };

  useEffect(() => {
    getUsersData();
  }, []);

  // Tab 3 - Content
  const [content, setContent] = useState([]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newContent, setNewContent] = useState({
    Title: '',
    textContent: '',
    imgRef: '',
    imgAlt: '',
  });

  const getContentData = async () => {
    const data = await fetch('http://localhost:5000/api/Content', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setContent(data);
  };

  const deleteContent = async (contentId) => {
    // ...
  };

  const handleAddFormSubmit = async (event) => {
    event.preventDefault();
    await fetch('http://localhost:5000/api/Content', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(newContent),
    });
    setShowAddForm(false);
    getContentData();
    setNewContent({
      Title: '',
      textContent: '',
      imgRef: '',
      imgAlt: '',
    });
  };

  useEffect(() => {
    getContentData();
  }, []);

  // Tab 4 - Preview
  const [previewContent, setPreviewContent] = useState([]);

  const getPreviewData = async () => {
    const data = await fetch('http://localhost:5000/api/previewContent', {
      method: 'GET',
      headers: {
        'content-type': 'application/json',
        Accept: 'application/json',
      },
    }).then((res) => res.json());
    console.log(data);
    setPreviewContent(data);
  };

  const deletePreview = async (previewId) => {
    // ...
  };

  useEffect(() => {
    getPreviewData();
  }, []);

  return (
    <div>
      <Header />
      <Navbar />
      <div className="tabs-container">
        <div className="tab-header">
          {/* Tab headers */}
          <div className={`tab ${activeTab === 0 ? 'active' : ''}`} onClick={() => handleTabClick(0)}>
            Tab 1 employees
          </div>
          <div className={`tab ${activeTab === 1 ? 'active' : ''}`} onClick={() => handleTabClick(1)}>
            Tab 2 - users
          </div>
          <div className={`tab ${activeTab === 2 ? 'active' : ''}`} onClick={() => handleTabClick(2)}>
            Tab 3 - content
          </div>
          <div className={`tab ${activeTab === 3 ? 'active' : ''}`} onClick={() => handleTabClick(3)}>
            Tab 4 - preview
          </div>
        </div>

        {/* Tab content */}
        <div className="tab-content">
          {/* Content for Tab 1 - Employees */}
          {activeTab === 0 && (
            <div className="App">
              {/* ... */}
            </div>
          )}

          {/* Content for Tab 2 - Users */}
          {activeTab === 1 && (
            <div className="App">
              {/* ... */}
            </div>
          )}

          {/* Content for Tab 3 */}
          {activeTab === 2 && (
            <div className="App">
              <button onClick={getContentData}>Get Content</button>
              <table>
                {/* ... */}
              </table>
              <button onClick={() => setShowAddForm(true)}>Add Content</button>
              {showAddForm && (
                <div className="add-form">
                  <h2>Add Content</h2>
                  <form onSubmit={handleAddFormSubmit}>
                    {/* Form inputs */}
                    <div>
                      <label>Title:</label>
                      <input
                        type="text"
                        value={newContent.Title}
                        onChange={(e) => setNewContent({ ...newContent, Title: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Text Content:</label>
                      <textarea
                        value={newContent.textContent}
                        onChange={(e) => setNewContent({ ...newContent, textContent: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Image Reference:</label>
                      <input
                        type="text"
                        value={newContent.imgRef}
                        onChange={(e) => setNewContent({ ...newContent, imgRef: e.target.value })}
                      />
                    </div>
                    <div>
                      <label>Image Alt:</label>
                      <input
                        type="text"
                        value={newContent.imgAlt}
                        onChange={(e) => setNewContent({ ...newContent, imgAlt: e.target.value })}
                      />
                    </div>
                    <div className="form-buttons">
                      <button type="submit">Submit</button>
                      <button type="button" onClick={() => setShowAddForm(false)}>
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              )}
            </div>
          )}

          {/* Content for Tab 4 */}
          {activeTab === 3 && (
            <div className="App">
              {/* ... */}
            </div>
          )}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Contact;
