import React, { useState, useContext, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import { AuthContext } from '../../pages/auth/Auth';
import useisAdmin from '../isAdmin/isAdmin'; 
import { DarkModeContext } from '../../pages/darkmode/DarkMode';
import './header.css';

const Navbar = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const [selectedLink, setSelectedLink] = useState(0); // Index of the selected link
  const { loggedIn, logout } = useContext(AuthContext);
  const isAdminUser = useisAdmin(); 
  const { darkMode, toggleDarkMode } = useContext(DarkModeContext)

  useEffect(() => {
    const handleKeydown = (event) => {
      if (!showNavbar) return;

      switch (event.key) {
        case 'ArrowLeft':
          setSelectedLink((prevLink) => (prevLink > 0 ? prevLink - 1 : 0));
          break;
        case 'ArrowRight':
          setSelectedLink((prevLink) =>
            prevLink < linkElements.length - 1 ? prevLink + 1 : linkElements.length - 1
          );
          break;
        case 'Enter':
          linkElements[selectedLink].click(); // Navigate to the selected link
          break;
        default:
          break;
      }
    };

    const linkElements = Array.from(document.querySelectorAll('.nav-elements ul li a'));

    window.addEventListener('keydown', handleKeydown);

    return () => {
      window.removeEventListener('keydown', handleKeydown);
    };
  }, [showNavbar, selectedLink]);

  const handleShowNavbar = () => {
    setShowNavbar(!showNavbar);
  };

  const handleLogout = () => {
    logout();
    window.location.reload();
  };

  const handleToggleDarkMode = () => {
    toggleDarkMode();
  };

  return (
    <div className={`navbar ${darkMode ? 'dark-mode' : ''}`}>
      <div className="logo"><h2>EVENT EIRE</h2></div>
      <div className="menu-icon" onClick={handleShowNavbar}>
        Menu
      </div>
      <div className={`nav-elements  ${showNavbar && 'active'}`}>
        <ul>
          <li>
            <NavLink to="/">Home</NavLink>
          </li>
          <li>
            <NavLink to="/qrReader">QR READER</NavLink>
          </li>
          <li>
            <NavLink to="/contactUs">CONTACT US</NavLink>
          </li>
          {isAdminUser && ( // Only show the Admin menu if the user is an admin
            <li>
              <NavLink to="/admin">ADMIN</NavLink>
            </li>
          )}
        </ul>
        <div className="dark-mode-button">
          <button onClick={handleToggleDarkMode}>Toggle Dark Mode</button>
        </div>

        <div className="login-logout">
          {loggedIn !== null ? (
            <>
              <span>logged: {loggedIn}</span>
              <NavLink to="/">
                <button onClick={handleLogout}>Logout</button>
              </NavLink>
            </>
          ) : (
            <>
              <NavLink to="/login">
                <button>Login</button>
              </NavLink>

              <NavLink to="/signup">
                <button>Signup</button>
              </NavLink>
            </>
          )}
        </div>
      </div>
    </div>// navbar
  );
};

export default Navbar;
