import { AES } from 'crypto-js';

const DECRYPTION_KEY = 'thisIsMyKey';

const decrypt = (encryptedData) => {
  try {
    const [ivHex, encryptedDataHex] = encryptedData.split(':');
    const iv = Buffer.from(ivHex, 'hex');
    const encryptedDataBuffer = Buffer.from(encryptedDataHex, 'hex');
    const decryptedData = AES.decrypt({ ciphertext: encryptedDataBuffer }, DECRYPTION_KEY, { iv });
    return decryptedData.toString(crypto.enc.Utf8);
  } catch (error) {
    console.error('Error decrypting data:', error);
    return null;
  }
};

export default decrypt;
