// export default Header

import { useState } from 'react';
import { NavLink } from 'react-router-dom';
import './header.css';

const Navbar = () => {
  const [showNavbar, setShowNavbar] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loggedInUser, setLoggedInUser] = useState(''); // New state variable for logged-in user

  const handleShowNavbar = () => {
    setShowNavbar(!showNavbar);
  };

  const handleLogout = () => {
    // Perform logout logic here
    setIsLoggedIn(false);
    setLoggedInUser('');
  };

  const handleLogin = (username) => {
    // Perform login logic here
    setIsLoggedIn(true);
    setLoggedInUser(username);
  };

  return (
    <nav className="navbar">
      <div className="container">
        <div className="logo">EVENT EIRE</div>
        <div className="menu-icon" onClick={handleShowNavbar}>
          Menu
        </div>
        <div className={`nav-elements  ${showNavbar && 'active'}`}>
          <ul>
            <li>
              <NavLink to="/">Home</NavLink>
            </li>
            <li>
              <NavLink to="/qrReader">QR READER</NavLink>
            </li>
            <li>
              <NavLink to="/contactUs">CONTACT US</NavLink>
            </li>
            <li>
              <NavLink to="/admin">ADMIN</NavLink>
            </li>
          </ul>
          <div className="login-logout">
            {isLoggedIn ? ( // Render username if user is logged in
              <div>
                <span>Welcome, {loggedInUser}</span>
                <button onClick={handleLogout}>Logout</button>
              </div>
            ) : ( // Otherwise, render "Login" and "Signup" buttons
              <>
                <NavLink to="/login">
                  <button>Login</button>
                </NavLink>
                <NavLink to="/signup">
                  <button>Signup</button>
                </NavLink>
              </>
            )}
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
