export const AuthProvider = ({ children }) => {
  const [authData, setAuthData] = useState({ loggedIn: null, darkMode: false });

  useEffect(() => {
    const storedAuthData = JSON.parse(localStorage.getItem('authData'));
    if (storedAuthData) {
      setAuthData(storedAuthData);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('authData', JSON.stringify(authData));
  }, [authData]);

  const login = (username) => {
    setAuthData({ ...authData, loggedIn: username });
  };

  const logout = () => {
    setAuthData({ ...authData, loggedIn: null });
  };

  const toggleDarkMode = () => {
    setAuthData({ ...authData, darkMode: !authData.darkMode });
  };

  return (
    <AuthContext.Provider value={{ ...authData, login, logout, toggleDarkMode }}>
      {children}
    </AuthContext.Provider>
  );
};
