import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Header from '../../components/header/Header';
import Footer from '../../components/footer/Footer';

const DataContent = () => {
  const { title } = useParams();
  const [content, setContent] = useState(null); 
  const [loading, setLoading] = useState(true); 
  const [error, setError] = useState(null); 

  useEffect(() => {

    const fetchContent = async () => {
      try {

        const response = await fetch(`/api/content?title=${title}`);
        const data = await response.json();

        if (response.ok) {
          setContent(data);
        } else {
          setError('This page does not exist.');
        }
      } catch (error) {
        setError('An error occurred while fetching the content.');
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, [title]);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  if (!content) {
    return null;
  }

  return (
    <div>
      <Header />
      <h1>Dynamic Content</h1>
      <p>Title: {content.Title}</p>
      <p>Text Content: {content.textContent}</p>
      <img src={content.imgRef} alt={content.imgAlt} />
      {/* Add your custom content here */}
      <Footer />
    </div>
  );
};

export default DataContent;

